mapper.select:
select ds.id, ds.current_appointments, ds.status, ds.start_time, ds.end_time, ds.schedule_date, ds.time_slot, ds.room_number, ds.schedule_type, ds.notes, u.name as doctorName, d.name as departmentName from doctor_schedule ds join user u on ds.doctor_id = u.id join department d on ds.department_id = d.id
ds.doctor_id = #{doctorScheduleDTO.doctorId}
and ds.department_id = #{doctorScheduleDTO.departmentId}
and ds.schedule_date = #{doctorScheduleDTO.scheduleDate}
and ds.time_slot = #{doctorScheduleDTO.timeSlot}
and ds.status = #{doctorScheduleDTO.status}
and ds.schedule_type = #{doctorScheduleDTO.scheduleType}
order by ds.schedule_date, ds.time_slot

select ds.id, ds.current_appointments, ds.status, dp.image, ds.start_time, ds.end_time, ds.schedule_type, ds.schedule_date, ds.time_slot, ds.room_number, ds.notes, ds.doctor_id as doctorId, u.name as doctorName, rf.fee as price, d.name as departmentName from doctor_schedule ds join user u on ds.doctor_id = u.id join department d on ds.department_id = d.id join doctor_profile dp on u.id = dp.user_id join registration_fee rf on ds.schedule_type = rf.type
ds.id = #{id}
order by ds.schedule_date, ds.time_slot

select ds.id, ds.current_appointments, ds.status, ds.doctor_id as doctorId, ds.start_time, ds.end_time, dp.image, ds.schedule_type, ds.schedule_date, ds.time_slot, ds.room_number, ds.notes, rf.fee as price, u.name as doctorName, d.name as departmentName from doctor_schedule ds join user u on ds.doctor_id = u.id join department d on ds.department_id = d.id join doctor_profile dp on u.id = dp.user_id join registration_fee rf on ds.schedule_type = rf.type
ds.department_id = #{departmentId}
and ds.schedule_date = #{date}
and ds.end_time > #{now}

select ds.id, ds.current_appointments, ds.status, ds.doctor_id as doctorId, ds.start_time, ds.end_time, ds.schedule_type, ds.schedule_date, ds.time_slot, ds.room_number, ds.notes, rf.fee as price, u.name as doctorName, d.name as departmentName from doctor_schedule ds join user u on ds.doctor_id = u.id join department d on ds.department_id = d.id join doctor_profile dp on u.id = dp.user_id join registration_fee rf on ds.schedule_type = rf.type
and ds.schedule_date between #{startDate} and #{endDate}
and ds.doctor_id = #{id}
order by ds.schedule_date, ds.time_slot
