mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM order_info
    WHERE patient_id = #{patientId}
    AND schedule_id = #{scheduleId}
    AND status = 0
)

SELECT *
FROM order_info


        patient_id = #{patientId}


        AND status = #{status}


        AND create_time >= #{startDate}


ORDER BY create_time DESC

SELECT order_info.order_no AS orderNo, patient.name AS patientName, order_info.amount AS amount, order_info.status AS status, order_info.pay_time AS payTime, order_info.pay_way AS payWay FROM order_info LEFT JOIN patient ON order_info.patient_id = patient.id
order_info.order_no LIKE CONCAT('%', #{orderNo}, '%')
AND order_info.status = #{status}
AND order_info.create_time between #{startDate} AND #{endDate}
AND patient.name LIKE CONCAT('%', #{patientName}, '%')
ORDER BY order_info.create_time DESC
