mapper.select:
SELECT * FROM message

    AND is_deleted = 0

        AND user_patient_id = #{userPatientId}


        AND status = #{status}


ORDER BY create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1) FROM message
AND is_deleted = 0 AND status = '未读'
AND user_patient_id = #{userPatientId}

SELECT COUNT(1) FROM message

    AND is_deleted = 0

        AND user_patient_id = #{userPatientId}


        AND status = #{status}
