mapper.select:
SELECT m.* ,
       p.name AS patientName
FROM message m left join patient p ON m.patient_id = p.id

    AND m.is_deleted = 0

        AND m.user_patient_id = #{userPatientId}


        AND m.status = #{status}


ORDER BY m.create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1) FROM message
AND is_deleted = 0 AND status = '未读'
AND user_patient_id = #{userPatientId}

SELECT COUNT(1) FROM message

    AND is_deleted = 0

        AND user_patient_id = #{userPatientId}


        AND status = #{status}
