mapper.select:
SELECT * FROM staff_message

    receiver_id = #{doctorId}
    AND is_deleted = 0

        AND status = #{status}


        AND message_type = #{type}


        AND (title LIKE CONCAT('%', #{q}, '%') OR content LIKE CONCAT('%', #{q}, '%'))


ORDER BY pinned DESC, create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1) FROM staff_message

    receiver_id = #{doctorId}
    AND is_deleted = 0

        AND status = #{status}


        AND message_type = #{type}


        AND (title LIKE CONCAT('%', #{q}, '%') OR content LIKE CONCAT('%', #{q}, '%'))
